import { useEffect, useState } from 'react';
import './styles.css'
import ProductList from '../../Components/ProductList';
import  arroz  from  '../../assets/arroz.png'
function App() {

  const [product, setProduct] = useState("1");
  const [currentProduct, setCurrentProduct] = useState({});

  const handleGetProduct = async () => {


    const productData = await fetch(`http://localhost:8080/supermarket/api/v1/product/${product}`);
    const newProduct = await productData.json();

    if (newProduct.name){
      setCurrentProduct({
        name: newProduct.name,
        description: newProduct.description,
        price: newProduct.price
      })
    }
  }

  useEffect(() => {
    handleGetProduct();
  })

  return (
    <div className="App">
      <header>
      <h1>Supermarket</h1>
      <main>
        <ProductList productImage = {arroz} productTitle={currentProduct.name} productDescription={'Mortadela mista de porco e frango'} productPrice={9.99}></ProductList>
      </main>
      </header>
    </div>
  );
}

export default App;
