import { useEffect, useState } from 'react';
import './styles.css'
import ProductList from '../../Components/ProductList';
import  arroz  from  '../../assets/arroz.png'
function App() {
  const [inputValue, setInputValue] = useState("")
  const [product, setProduct] = useState("52");
  const [currentProduct, setCurrentProduct] = useState({});

  const handleGetProduct = async () => {


    const productData = await fetch(`http://localhost:8080/supermarket/api/v1/product/${product}`);
    const newProduct = await productData.json();

    if (newProduct.name){
      setCurrentProduct({
        name: newProduct.name,
        description: newProduct.description,
        price: newProduct.price,
        imageUrl: newProduct.imageUrl,
      })
    }
  }

  const handleInputChange = (event) => {
    setProduct(event.target.value);
  }

  useEffect(() => {
    handleGetProduct();
  })

  return (
    <div className="App">
      <header>
      <h1>Supermarket</h1>
      <main>
        <ProductList productImage = {currentProduct.imageUrl} productTitle={currentProduct.name} productDescription={currentProduct.description} productPrice={currentProduct.price}></ProductList>
        <div className='input-id'>
        <input value={product}></input>
        <button>Pesquisar</button>

        </div>

      </main>
      </header>
    </div>
  );
}

export default App;
